mapper.select:
SELECT a.accommodation_id AS accommodationId, a.name, a.ac_type, ct.code_name AS typeName, a.region_code, cr.code_name AS regionName, a.deleted_yn AS deletedYn, TO_CHAR(a.created_at, 'YYYY-MM-DD"T"HH24:MI:SS') AS createdAt FROM accommodation a LEFT JOIN common_code ct ON ct.code_id = a.ac_type AND ct.use_yn = 'Y' LEFT JOIN common_code cr ON cr.code_id = a.region_code AND cr.use_yn = 'Y'
AND a.region_code = #{regionCode}
AND a.ac_type = #{acType}
AND a.name LIKE '%' || #{keyword} || '%'
AND a.deleted_yn = #{deletedYn}
ORDER BY a.deleted_yn ASC, a.created_at DESC

SELECT a.accommodation_id AS accommodationId, a.name, a.ac_type, ct.code_name AS typeName, a.deleted_yn AS deletedYn, a.description, a.address, a.region_code, cr.code_name AS regionName, a.latitude, a.longitude, a.check_in_time AS checkInTime, a.check_out_time AS checkOutTime, TO_CHAR(a.created_at, 'YYYY-MM-DD"T"HH24:MI:SS') AS createdAt, TO_CHAR(a.updated_at, 'YYYY-MM-DD"T"HH24:MI:SS') AS updatedAt FROM accommodation a
LEFT JOIN common_code ct ON ct.code_id = a.ac_type AND ct.use_yn = 'Y' LEFT JOIN common_code cr ON cr.code_id = a.region_code AND cr.use_yn = 'Y'
WHERE a.accommodation_id = #{accommodationId}
