mapper.select:
SELECT COUNT (*) FROM board b LEFT JOIN common_code c ON b.region_code = c.code_id AND c.parent_id = 'region_type' WHERE b.deleted = 'N' AND b.board_type = #{boardType}
And b.region_code = #{regionCode}
AND (b.title LIKE '%' || #{keyword} || '%' OR b.count LIKE '%' || #{keyword} || '%')

SELECT * FROM ( SELECT inner_query.*, ROWNUM AS rn FROM ( SELECT b.board_type AS boardType, b.region_code AS regionCode, cc.code_name AS regionName, b.board_id AS boardId, b.accommodation_id AS accommodationId, a.name AS accommodationName, b.booking_id AS bookingId, b.user_id AS userId, u.nickname AS userNickName, b.title, b.rating, b.views_count AS viewsCount, TO_CHAR(b.created_at, 'YYYY-MM-DD HH24:MI:SS') AS createdAt, TO_CHAR(b.updated_at, 'YYYY-MM-DD HH24:MI:SS') AS updatedAt, b.likes_count AS likesCount, b.deleted
FROM board b LEFT JOIN accommodation a ON b.accommodation_id = a.accommodation_id LEFT JOIN users u ON b.user_id = u.user_id LEFT JOIN common_code cc ON b.region_code = cc.code_id
WHERE b.deleted = 'N' AND b.board_type = #{boardType}
AND b.region_code IN
#{code}
ORDER BY b.likes_count DESC, b.created_at DESC
ORDER BY b.views_count DESC, b.created_at DESC
ORDER BY b.created_at DESC
) inner_query ) WHERE rn BETWEEN (#{pageNum}-1) * #{pageSize} + 1 AND #{pageNum} * #{pageSize}

SELECT user_id
FROM board
WHERE board_id = #{boardId}

mapper.update:
UPDATE board b SET b.likes_count = (
    SELECT COUNT(*)
    FROM likes l
    WHERE l.board_id = b.board_id
)
WHERE b.board_id = #{boardId}
