mapper.update:
UPDATE users
nickname = #{nickname}, name = #{name}, email = #{email}, phone = #{phone}, profile_image = #{profileImage}, birth_date = #{birthDate}, gender = #{gender}, updated_at = CURRENT_TIMESTAMP
WHERE user_id = #{userId}

UPDATE users SET status = #{status},
withdrawn_at = CURRENT_TIMESTAMP,
updated_at = CURRENT_TIMESTAMP WHERE user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM users
WHERE status = #{status}
